var Villa = function (code, label, max, selected) {
  return {
    'code': code,
    'label': label,
    'max': max,
    'selected': selected
  }
}

var Price = function (value, code, currency, title) {
  return {
    'value':value,
    'currency': currency,
    'unit': {
      'code': code,
      'title': title
    }
  }
}

var Selection = function (code, sDate, duration, title, price) {
  return {
    'code': code,
    'date': sDate,
    'duration': duration,
    'title': title,
    'price': price
  }
}

var Field = function (code, title, regexp) {
  return {
    'code': code,
    'title': title,
    'regexp': regexp
  }
}

var ExtraListItem = function (code, title, extraItems) {
  return {
    'code': code,
    'title': title,
    'extraItems': extraItems
  }
}

module.exports = {
  Villa: Villa,
  Selection: Selection,
  Field: Field,
  ExtraListItem: ExtraListItem,
  Price: Price
}