var mongoose = require('mongoose');
var Schema = mongoose.Schema;

var BasketPostSchema = new Schema({
  "extras": [
    {
      "code": String,
      "quantity": Number,
      "date": String,
      "duration": Number,
      "itemToken": String,
      "fields": [
        {
          "code": String,
          "value": Number
        }
      ]
    }
  ],
  "services": [
    {
      "villa": String,
      "code": String,
      "quantity": Number,
      "itemToken": String
    }
  ],
  "preference": {
    "villa": String,
    "preferences": [
      String
    ]
  }
});

module.exports = BasketPostSchema;