var mongoose = require('mongoose');
var Schema = mongoose.Schema;

var BasketSchema = new Schema({
  "total": {
    "value": Number,
    "currency": String,
    "unit": {
      "code": String,
      "title": String
    }
  },
  "modes": [
    {
      "code": String,
      "dates": [
        {
          "date": String,
          "price": {
            "value": Number,
            "currency": String,
            "unit": {
              "code": String,
              "title": String
            }
          }
        }
      ]
    }
  ]
});

module.exports = BasketSchema;