var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var SelectionSchema = require('./selection');
var FieldSchema = require('./field');

var ExtraSchema = new Schema({
  code: {
    type: String,
    unique: true
  },
  codeTheme: {
    type: String
  },
  theme: {
    type: String
  },
  codeCategory: {
    type: String
  },
  category: {
    type: String
  },
  title: {
    type: String
  },
  description: {
    type: String
  },  
  image: {
    normal: String,
    thumbnail : String
  },
  selections : [SelectionSchema],
  fields: [FieldSchema]
});

module.exports = ExtraSchema;