var mongoose = require('mongoose');
var Schema = mongoose.Schema;

var PreferenceSchema = new Schema({
  code: {
    type: String,
    unique: true
  },
  title: {
    type: String
  },
  enabled: {
    type: Boolean,
    default: true
  },
  selected: {
    type: Boolean,
    default: false
  }
});

module.exports = PreferenceSchema;