var mongoose = require('mongoose');
var Schema = mongoose.Schema;

var SelectionSchema = new Schema({
  code: {
    type: String,
    unique: true
  },
  date: {
    type: String
  },
  duration: {
    type: Number
  },
  title: {
    type: String
  },
  price: {
    type: Schema.Types.Mixed 
  },
});

module.exports = SelectionSchema;