var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var VillaSchema = require('./villa');
var SelectionSchema = require('./selection');

var ServiceSchema = new Schema({
  code: {
    type: String,
    unique: true
  },
  title: {
    type: String
  },
  description: {
    type: String
  },
  price: {
    type: Schema.Types.Mixed 
  },
  image: {
    normal: String,
    thumbnail : String
  },  
  villas: [VillaSchema]
});

module.exports = ServiceSchema;