var mongoose = require('mongoose');
var Schema = mongoose.Schema;

var VillaSchema = new Schema({
  code: {
    type: String,
    unique: true
  },
  label: {
    type: Number
  },
  max: {
    type: Number
  },  
  selected: {
    type: Boolean,
    default: false
  }
});

module.exports = VillaSchema;